mapper.select:
select 0 isChildren,se.id, se.sourceID, se.sourceParent,se.userID, se.type, se.desc, se.createTime , u.avatar, CASE WHEN u.nickname is null or u.nickname = '' THEN u.name else u.nickname end nickname, u.sex,u.status, s.isFolder, s.name from io_source_event se inner JOIN io_source s ON se.sourceID = s.sourceID left join users u on se.userID = u.userID and u.status in (0,1) where se.sourceID = #{sourceID}
union all select 1 isChildren,se.id, se.sourceID, se.sourceParent,se.userID, se.type, se.desc, se.createTime , u.avatar, CASE WHEN u.nickname is null or u.nickname = '' THEN u.name else u.nickname end nickname, u.sex,u.status, s.isFolder, s.name from io_source_event se inner JOIN io_source s ON se.sourceID = s.sourceID left join users u on se.userID = u.userID and u.status in (0,1) where se.sourceParent = #{sourceID}
order by createTime desc, id desc
